CREATE TABLE IF NOT EXISTS players ( `uid` INT NOT NULL PRIMARY KEY, `firstname` varchar(20) NOT NULL  DEFAULT '', `name` varchar(20) NOT NULL UNIQUE, `lastname` varchar(20) NOT NULL DEFAULT '', `level` smallint DEFAULT NULL, `breed` varchar(20) NOT NULL DEFAULT '', `gender` varchar(20) NOT NULL DEFAULT '', `faction` varchar(20) NOT NULL DEFAULT '', `prof` varchar(20) NOT NULL DEFAULT '', `prof_title` varchar(20) NOT NULL DEFAULT '', `ai_rank` varchar(20) NOT NULL DEFAULT '', `ai_level` smallint DEFAULT NULL, `org_id` int DEFAULT NULL, `org` varchar(255) NOT NULL DEFAULT '', `org_rank`  varchar(20) NOT NULL DEFAULT '', `org_rank_id` smallint DEFAULT NULL, `dimension` smallint, `source`  varchar(50) NOT NULL DEFAULT '', `last_update` INT );