DROP TABLE IF EXISTS `ofabarmor`;
CREATE TABLE `ofabarmor` (`profession` VARCHAR(30) NOT NULL, `name` VARCHAR(150) NOT NULL, `slot` VARCHAR(30) NOT NULL, `lowid` int(11) NOT NULL, `highid` int(11) NOT NULL, `upgrade` int(11) NOT NULL);




DROP TABLE IF EXISTS `ofabarmorcost`;
CREATE TABLE `ofabarmorcost` (`slot` varchar(255) NOT NULL, `ql` int(11) NOT NULL, `vp` int(11) NOT NULL);












DROP TABLE IF EXISTS `ofabarmortype`;
CREATE TABLE `ofabarmortype` (`type` SMALLINT NOT NULL, `profession` VARCHAR(30) NOT NULL);

DROP TABLE IF EXISTS `clumpref`;
CREATE TABLE `clumpref` ( `lid` int(11) NOT NULL default '0', `hid` int(11) NOT NULL default '0', `name` varchar(255) NOT NULL default '', `specials` varchar(255) NOT NULL default '', `type` int(11) NOT NULL default '0', `spec` tinyint(4) NOT NULL default '0');

DROP TABLE IF EXISTS `clumpweapon`;
CREATE TABLE `clumpweapon` ( `type` int(11) NOT NULL default '0', `name` varchar(255) NOT NULL default '', `0` int(11) NOT NULL default '0', `99` int(11) NOT NULL default '0', `100` int(11) NOT NULL default '0', `199` int(11) NOT NULL default '0', `200` int(11) NOT NULL default '0', `299` int(11) NOT NULL default '0', `300` int(11) NOT NULL default '0');
