<?phpif (preg_match("/^random (.+)$/i", $message, $arr)){	$grouptext = trim($arr[1]);	$text = split(" ", $grouptext);	$low = 0; $high = count($text)-1;	while(1) {		$random = rand($low, $high);		if (!isset($marked[$random])) {			$count++;			$newtext .= " $count: ".$text[$random];			$marked[$random] = 1;			if (count($marked) == count($text)) {				break;			}		}		$i = $low;		while(1) {			if ($marked[$i] != 1) {				$low = $i;				break;			} else {				$i++;			}		}		$i = $high;		while(1) {			if ($marked[$i] != 1) {				$high = $i;				break;			} else {				$i--;			}		}	}    	$this->send($newtext, $sendto);}?>