INSERT INTO `recipes` VALUES (102,2,'Repairman''s Hat','','#C16------------------------------\n#C12Ingredients#C20\n#C16------------------------------#C20\n\nSome Notum \n#L "Notum Fragment" "136624"\n<img src=rdb://25795>\n#C15or#C20\n#L "Notum Chip" "136622"\n<img src=rdb://25794>\n\n#L "Salesman''s Hat" "205738"\n<img src=rdb://205771>\n\n#L "Nano Programming Interface" "161699"\n<img src=rdb://99279>\n\n#L "Inactive OT Metamorphing Liquid Nanobots" "164952"\n<img src=rdb://156496>\n\n#L "Field Quantum Physics All-Purpose Tool" "164558"\n<img src=rdb://20410>\n\n#C16------------------------------\n#C12Recipe#C16\n#C16------------------------------#C20\n\nField Quantum Physics All-Purpose Tool\n#C15+#C20\nSalesman''s Hat\n#C15=#C20\n<img src=rdb://205772>\n#L "Transformed Salesman''s Hat" "205741"\n#C16Skills: | NP,CH |#C20\n\n#C16------------------------------#C20\n\nNano Programming Interface\n#C15+#C20\nInactive OT Metamorphing Liquid Nanobots\n#C15=#C20\n<img src=rdb://156492>\n#L "Activated OT Metamorphing Liquid Nanobots" "164954"\n#C16Skills: | NP,CH |#C20\n\n#C16------------------------------#C20\n\nNotum\n#C15+#C20\nActivated OT Metamorphing Liquid Nanobots\n#C15=#C20\n<img src=rdb://164949>\n#L "Stabilized OT Metamorphing Liquid Nanobots" "164956"\n#C16Skills: | NP,CH |#C20\n\n#C16------------------------------#C20\n\nStabilized OT Metamorphing Liquid Nanobots\n#C15+#C20\nTransformed Salesman''s Hat\n#C15=#C20\n<img src=rdb://205772>\n#L "Repairman''s Hat" "205743"\n#C16Skills: | NP,CH |#C20\n\n#C16------------------------------\n#C12Comments#C16\n#C16------------------------------#C20\n\nYou can right-clic it to upgrad it up to QL50.\n');
INSERT INTO `recipes` VALUES (103,4,'Restoration Kit','','#C16------------------------------\n#C12Ingredients#C20\n#C16------------------------------#C20\n\n#L "Mimicking Cellular Oil" "154268"\n<img src=rdb://100306>\n\n#L "Receptive Bots" "154266"\n<img src=rdb://11755>\n\n#L "Shining Nano Knot of Restoration" "154270"\n<img src=rdb://19860>\n\n#C16------------------------------\n#C12Recipe#C16\n#C16------------------------------#C20\n\nMimicking Cellular Oil\n#C15+#C20\nReceptive Bots\n#C15=#C20\n<img src=rdb://11754>\n#L "Purple Glowing Stim" "154274"\n#C16Skills: |  PT  |#C20\n\n#C16------------------------------#C20\n\nShining Nano Knot of Restoration\n#C15+#C20\nPurple Glowing Stim\n#C15=#C20\n<img src=rdb://156492>\n#L "Restoration Kit" "154272"\n#C16Skills: |  PT  |#C20\n\n#C16------------------------------\n#C12Comments#C16\n#C16------------------------------#C20\n\nVery useful on the paper, recovering Nanopoints and Lifepoints... But ridiculously expensive, this recipe will cost you all your exploited credits...\n');
INSERT INTO `recipes` VALUES (104,2,'Rollerrat Helmets','','#C16------------------------------\n#C12Ingredients#C20\n#C16------------------------------#C20\n\n#L "screwdriver" "150922"\n<img src=rdb://151011>\n#C15or#C20\n#L "Mass Relocating Robot (Shape Hard Armor)" "162218" (#L "recipe" "/tell <myname> rshow 30")\n<img src=rdb://163856>\n\n#L "Hacker Tool" "87814"\n<img src=rdb://99282>\n\n#L "B.B.I. - Nano Enhanced B-12 Tanning Acid" "162247"\n<img src=rdb://156490>\n\n#L "Undamaged Piece of Rubbery Rollerrat Flesh" "206886"\n<img src=rdb://144702>\n\n#L "MasterComm - Personalization Device" "156025"\n<img src=rdb://11618>\n\n#L "Ancarim Sun Tan Lotion" "205874"\n<img src=rdb://156504>\n\n#L "Carbonum Plate Helmet" "162437"  (#L "recipe" "/tell <myname> rshow 30")\n<img src=rdb://10833>\n\n#C16------------------------------\n#C12Recipe#C16\n#C16------------------------------#C20\n\nB.B.I. - Nano Enhanced B-12 Tanning Acid\n#C15+#C20\nUndamaged Piece of Rubbery Rollerrat Flesh\n#C15=#C20\n<img src=rdb://144701>\n#L "Tanned Piece of Rollerrat Flesh" "206890"\n#C16Skills: | AG,CH |#C20\n\n#C16------------------------------#C20\n\nHacker Tool\n#C15+#C20\nMasterComm - Personalization Device\n#C15=#C20\n<img src=rdb://11606>\n#L "Communication Device" "206891"\n#C16Skills: |  BE  |#C20\n\n#C16------------------------------#C20\n\nCommunication Device\n#C15+#C20\nTanned Piece of Rollerrat Flesh\n#C15=#C20\n<img src=rdb://205768>\n#L "Fragile Rollerrat Helmet" "206892"\n#C16Skills: |  BE  |#C20\n\n#C16------------------------------#C20\n\nAncarim Sun Tan Lotion\n#C15+#C20\nFragile Rollerrat Helmet\n#C15=#C20\n<img src=rdb://205768>\n#L "Simple Rollerrat Helmet" "206876"\n#C16Skills: |  BE  |#C20\n\nYou can use 9 another Tanned Piece of Rollerrat Flesh on your Simple Rollerrat Helmet, then you get ...\n\n#L "Excellent Rollerrat Helmet" "206885"\n<img src=rdb://205768>\n\n#C16------------------------------#C20\n\nScrewdriver\n#C15+#C20\nExcellent Rollerrat Helmet\n#C15=#C2