INSERT INTO `recipes` VALUES (597,1,'Ancient Damage Generation Device','','#C16------------------------------\n#C12Ingredients#C20\n#C16------------------------------#C20\n\n#L "Ancient Medical Device" "267736" #L "Recipe" "/tell <myname> rshow 598"\n<img src=rdb://218775>\n\n#L "Ancient Damage Generation Device" "267725"\n<img src=rdb://218770>\n\nand <highlight>one<end> of the following items:\n\n#L "Acid Gland Sample" "267743"\n<img src=rdb://144703>\n\n#L "Fire Gland Sample" "267741"\n<img src=rdb://144705>\n\n#L "Frost Gland Sample" "267742"\n<img src=rdb://144702>\n\n#L "Radioactive Gland Sample" "267744"\n<img src=rdb://144705>\n\n#L "Venom Gland Sample" "267745"\n<img src=rdb://253010>\n\n#C16------------------------------\n#C12Recipe#C16\n#C16------------------------------#C20\n\nAncient Medical Device\n#C15+#C20\na gland sample (eg: Acid Gland Sample)\n#C15=#C20\n<img src=rdb://19860>\na sample of compound\n(eg: #L "Sample of a chemical based compound" "267732")\n#C16Skills: | PT1750 |#C20\n\n#C16------------------------------#C20\n\nAncient Damage Generation Device\n#C15+#C20\nsample of compound (eg: Sample of a chemical based compound)\n#C15=#C20\n<img src=rdb://19860>\nAncient Generation Device\n(eg: #L "Ancient Chemical Generation Device" "267722")\n#C16Skills: | CL1250 |#C20\n\n#C16------------------------------\n#C12Details#C16\n#C16------------------------------#C20\n\nAcid Gland Sample = #L "Ancient Chemical Generation Device" "267722"\nFire Gland Sample = #L "Ancient Fire Generation Device" "267720"\nFrost Gland Sample = #L "Ancient Cold Generation Device" "267721"\nRadioactive Gland Sample = #L "Ancient Radiation Generation Device" "267723"\nVenom Gland Sample = #L "Ancient Poison Generation Device" "267724"\n\n#C16------------------------------\n#C12Comments#C16\n#C16------------------------------#C20\n\nFinal product is used to change your damage type as well as give you 500 ACs of that type. :D\n');
