INSERT INTO recipes (id, name, author, recipe) VALUES (117, 'Cutting Valuable Stones', '', '#C16------------------------------\n#C12Ingredients#C20\n#C16------------------------------#C20\n\na Valuable Stone\n(ie: #L "Gem" "136641")\n<img src=rdb://136593>\n\n#L "Jensen Gem Cutter" "151366"\n<img src=rdb://149941>\n\n#C16------------------------------\n#C12Recipe#C16\n#C16------------------------------#C20\n\nJensen Gem Cutter\n#C15+#C20\na Valuable Stone\n#C15=#C20\n<img src=rdb://136593>\nPerfectly Cut Valuable Stone\n(ie:#L "Perfectly Cut Gem" "151552")\n#C16Skills: |  ME  |#C20\n\n#C16------------------------------\n#C12Comments#C16\n#C16------------------------------#C20\n\nThis process transforms the Valuable Stone into a Perfectly Cut one, thus doubling its value. The result can also be used in the making of #L "Jewels" "/tell <myname> rshow 62".\n');
