INSERT INTO recipes (id, name, author, recipe) VALUES (597, 'Ancient Damage Generation Device', '', '#C16------------------------------\n#C12Ingredients#C20\n#C16------------------------------#C20\n\n#L "Ancient Medical Device" "267736" #L "Recipe" "/tell <myname> rshow 598"\n<img src=rdb://218775>\n\n#L "Ancient Damage Generation Device" "267725"\n<img src=rdb://218770>\n\nand <highlight>one<end> of the following items:\n\n#L "Acid Gland Sample" "267743"\n<img src=rdb://144703>\n\n#L "Fire Gland Sample" "267741"\n<img src=rdb://144705>\n\n#L "Frost Gland Sample" "267742"\n<img src=rdb://144702>\n\n#L "Radioactive Gland Sample" "267744"\n<img src=rdb://144705>\n\n#L "Venom Gland Sample" "267745"\n<img src=rdb://253010>\n\n#C16------------------------------\n#C12Recipe#C16\n#C16------------------------------#C20\n\nAncient Medical Device\n#C15+#C20\na gland sample (eg: Acid Gland Sample)\n#C15=#C20\n<img src=rdb://19860>\na sample of compound\n(eg: #L "Sample of a chemical based compound" "267732")\n#C16Skills: | PT1750 |#C20\n\n#C16------------------------------#C20\n\nAncient Damage Generation Device\n#C15+#C20\nsample of compound (eg: Sample of a chemical based compound)\n#C15=#C20\n<img src=rdb://19860>\nAncient Generation Device\n(eg: #L "Ancient Chemical Generation Device" "267722")\n#C16Skills: | CL1250 |#C20\n\n#C16------------------------------\n#C12Details#C16\n#C16------------------------------#C20\n\nAcid Gland Sample = #L "Ancient Chemical Generation Device" "267722"\nFire Gland Sample = #L "Ancient Fire Generation Device" "267720"\nFrost Gland Sample = #L "Ancient Cold Generation Device" "267721"\nRadioactive Gland Sample = #L "Ancient Radiation Generation Device" "267723"\nVenom Gland Sample = #L "Ancient Poison Generation Device" "267724"\n\n#C16------------------------------\n#C12Comments#C16\n#C16------------------------------#C20\n\nFinal product is used to change your damage type as well as give you 500 ACs of that type. :D\n');
INSERT INTO recipes (id, name, author, recipe) VALUES (598, 'Ancient Medical Device', '', '#C16------------------------------\n#C12Ingredients#C20\n#C16------------------------------#C20\n\n#L "Energy Infused Crystal" "267749"\n<img src=rdb://156567>\n\n#L "Inactive Ancient Medical Device" "267735"\n<img src=rdb://218774>\n\n#L "Crystalised Memories of a Doctor" "267726"\n<img src=rdb://72771>\n\n#C16------------------------------\n#C12Recipe#C16\n#C16------------------------------#C20\n\nEnergy Infused Crystal\n#C15+#C20\nInactive Ancient Medical Device\n#C15=#C20\n<img src=rdb://218774>\n#L "Active Ancient Medical Device" "267914"\n#C16Skills: | ME1500 |#C20\n\n#C16------------------------------#C20\n\nActive Ancient Medical Device\n#C15+#C20\nCrystalised Memories of a Doctor\n#C15=#C20\n<img src=rdb://218775>\n#L "Ancient Medical Device" "267736"\n#C16Skills: | ME1500 |#C20\n\n#C16------------------------------\n#C12Comments#C16\n#C16------------------------------#C20\n\nAnother handy reusable tool for tradeskillers.\n');
INSERT INTO recipes (id, name, author, recipe) VALUES (600, 'Frozen Yutto Remedies', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 With items gathered during the Penumbra quests and found in Alappaa, you can create 3 different kinds of Yutto Remedies. The base for all is the same, just the last components differ depending which type you wish to build. \nNote: Every step requires 1100 in Pharmacy skill and every product comes in stack of 5.\n#C12 *** #C20\n\n#L "Yuttos Filter" "269884"\n<img src=rdb://205505>\n\n#L "Permafrost Residue" "269872"\n<img src=rdb://72768>\n\n#L "Empty Permafrost Stimulant Chamber" "269891"\n<img src=rdb://100323>\n\n#L "Volatile Permafrost Chemicals" "269893"\n<img src=rdb://100330>\n#C15or#C20\n#L "Viscous Permafrost Chemicals" "269894"\n<img src=rdb://100337>\n#C15or#C20\n#L "Regenerative Permafrost Chemicals" "269895"\n<img src=rdb://100333>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n<highlight>The Base:<end>\n\nYuttos Filter\n#C15+#C20\nPermafrost Residue\n#C15=#C20\n<img src=rdb://100323>\n#L "Filtered Permafrost Residue" "269890"\n#C16Skills: | PH |#C20\n\nFiltered Permafrost Residue\n#C15+#C20\nEmpty Permafrost Stimulant Chamber\n#C15=#C20\n<img src=rdb://100315>\n#L "Full Permafrost Stimulant Chamber" "269892"\n#C16Skills: | PH |#C20\n\n#C14------------------------------#C20\n\n<highlight>The variations:<end>\n\n#C16Cold damage#C20\nFull Permafrost Stimulant Chamber\n#C15+#C20\nVolatile Permafrost Chemicals\n#C15=#C20\n<img src=rdb://100314>\n#L "Frozen Yuttos Remedy - Bane" "269865"\n#C16Skills: |  PH  |#C20\n#C16Note: Runs Vengeful Ward of Penumbra in your NCU (+50 cold damage, +500 Cold AC)#C20\n#C14------------------------------#C20\n\n#C16Add all defence#C20\nFull Permafrost Stimulant Chamber\n#C15+#C20\nViscous Permafrost Chemicals\n#C15=#C20\n<img src=rdb://100314>\n#L "Frozen Yuttos Remedy - Icy Scales" "269866"\n#C16Skills: |  PH  |#C20\n#C16Note: Runs Protective Ward of Penumbra in your NCU (+5 AAD, +500 Cold AC)#C20\n#C14------------------------------#C20\n\n#C16Heal and Nano Delta#C20\nFull Permafrost Stimulant Chamber\n#C15+#C20\nRegenerative Permafrost Chemicals\n#C15=#C20\n<img src=rdb://100314>\n#L "Frozen Yuttos Remedy - Tonic" "269867"\n#C16Skills: |  PH  |#C20\n#C16Note: Runs Regenerative Ward of Penumbra in your NCU (+2 heal delta, +2 nano delta, +500 Cold AC)#C20\n#C14------------------------------#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (601, 'Anun Membrane Gloves', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 Only Bureaucrats and Traders can use these gloves. Since the\ntradeskill process creates a nondrop item, the user must do it him/herself.\nBasic ingredient for gloves are Anun Wings. They drop of any Anun mob, for\nhigher quality I would suggest hunting Anuns in PW or inside missions.\nYou''ll also need Mass Relocating Robot (Shape Soft Armor) to complete the\nprocess.\n#C12 *** #C20\n\n#L "Anun Wing" "248930"\n<img src=rdb://144705>\n\n#L "Mass Relocating Robot (Shape Soft Armor)" "162220"\n<img src=rdb://163857>\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nAnun Wing\n#C15+#C20\nMass Relocating Robot (Shape Soft Armor)\n#C15=#C20\n<img src=rdb://159134>\n#L "Anun Membrane Gloves" "248928"\n#C16Skills: | requires 3.5 QL in Mech. Engi |#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (602, 'Ceremonial Chief''s Headwear', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 There are actually two types of them, made either from Small\nObsidian Jar or Obsidian Jar. Jars drops of random mobs in Inner Sanctum.\nOnly thing needed to complete the process is Advanced Hacker Tool, available\nfrom Devices Terminal.\n#C12 *** #C20\n\n#L "Small Obsidian Jar" "206477"\n<img src=rdb://119173>\n\n#L "Obsidian Jar" "206457"\n<img src=rdb://119185>\n\n#L "Advanced Hacker Tool" "87814"\n<img src=rdb://99282>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n<highlight>Ceremonial Watchman''s Hood<end>\n\nSmall Obsidian Jar\n#C15+#C20\nAdvanced Hacker Tool\n#C15=#C20\n<img src=rdb://205773>\n#L "Ceremonial Watchman''s Hood" "206459"\n#C16Skills: | Requires 665 in Breaking & Entering Skill.|#C20\n\n#C14------------------------------#C20\n\n<highlight>Ceremonial Chief''s Headwear<end>\n\nObsidian Jar\n#C15+#C20\nAdvanced Hacker Tool\n#C15=#C20\n<img src=rdb://205760>\n#L "Ceremonial Chief''s Headwear" "206455"\n#C16Skills: | Requires 855 in Breaking & Entering Skill. |#C20\n\n#C14------------------------------#C20\n\n#C16Note: Both headgears are leveling items (right-click on them to upgrade\nthem), Watchman''s between QL 140 and 180, Chief''s between QL 180 and 200.\nBoth are also title locked, Watchman''s at title 4 to 5 and Chief''s at title\n5#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (603, 'Cloak of the Reanimated', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 This cloak comes in 6 different types. Each are made in the\nsame way, apart from the Soul that determines the type of cloak you wish to\nmake.\nThe Reanimator''s Cloak can be found in the Crypt of Home dungeon and the\nitems to upgrade it to a Cloak of the Reanimated drop off the Escaped\nPrisoners in Milky Way.\nYou will only need 1 Soul to make the base cloak. To further upgrade it you\nwill need 4 of each type.\n#C12 *** #C20\n\n#L "Reanimator''s Cloak" "245260"\n<img src=rdb://22892>\n\n#L "Soul of the Jester" "274653"\n<img src=rdb://274674>\n\n#L "Soul of the Ranger" "274654"\n<img src=rdb://274675>\n\n#L "Soul of the Summoner" "274651"\n<img src=rdb://274677>\n\n#L "Soul of the Healer" "274656"\n<img src=rdb://274672>\n\n#L "Soul of the Gladiator" "274655"\n<img src=rdb://274670>\n\n#L "Soul of the Illusionist" "274652"\n<img src=rdb://274670>\n\n#L "Empty Spirit Vest" "274646"\n<img src=rdb://274678>\n\n#L "Soul Ripper" "274650"\n<img src=rdb://274684>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n<highlight>The Base Cloak of the Reanimated XXX:<end>\n\nReanimator''s Cloak\n#C15+#C20\nSoul Ripper\n#C15=#C20\n<img src=rdb://227530>\n#L "Explosion of Light" "274728"\n#C16Skills: | ? |#C20\n#C16Note: Right clicking the Explosion of light will result in:#C20\n\n#L "Soul of the Reanimator" "274649"\n<img src=rdb://274676>\n#C15and#C20\n#L "Grey Cloak" "274647"\n<img src=rdb://274671>\n\n#C12Note: At this point you will set the type of your Cloak. Use the Soul of\nXXX (any of the 6 types) on your Soul of the Reanimator to make a Cloak of\nthat type.#C20\n\nSoul of the Reanimator\n#C15+#C20\nSoul of the Jester\n#C15=#C20\n<img src=rdb://274674>\n#L "Soul of the Reanimated Jester" "274660"\n#C16Skills: | ? |#C20\n\nEmpty Spirit Vest\n#C15+#C20\nSoul of the Reanimated Jester\n#C15=#C20\n<img src=rdb://274686>\n#L "Jester''s Spirit Vest" "274668"\n\nGrey Cloak\n#C15+#C20\nJester''s Spirit Vest\n#C15=#C20\n<img src=rdb://274687>\n#L "Cloak of the Reanimated Jester (1/5)" "274718"\n\n#C14------------------------------#C20\n\n<highlight>Upgrading the Cloak of the Reanimated XXX:<end>\n\nSoul of the Ranger\n#C15+#C20\nSoul of the Gladiator\n#C15=#C20\n<img src=rdb://274673>\n#L "Dull Combined Soul Crystal" "274723"\n\nDull Combined Soul Crystal\n#C15+#C20\nSoul of the Healer\n#C15=#C20\n<img src=rdb://274673>\n#L "Dim Combined Soul Crystal" "274724"\n\nDim Combined Soul Crystal\n#C15+#C20\nSoul of the Summoner\n#C15=#C20\n<img src=rdb://274673>\n#L "Sparkling Combined Soul Crystal" "274725"\n\nSparkling Combined Soul Crystal\n#C15+#C20\nSoul of the Illusionist\n#C15=#C20\n<img src=rdb://274673>\n#L "Shining Combined Soul Crystal" "274726"\n\nShining Combined Soul Crystal\n#C15+#C20\nSoul of the Jester\n#C15=#C20\n<img src=rdb://274673>\n#L "Awakened Soul Crystal" "274727"\n\n#C16Repeat this step 4 times to end up with 4 Awakened Soul Crystals. Now\napply them to you cloak.#C20\n\nAwakened Soul Crystal\n#C15+#C20\nCloak of the Reanimated Jester (1/5)\n#C15=#C20\n<img src=rdb://274687>\n#L "Cloak of the Reanimated Jester (2/5)" "274719"\n\nAwakened Soul Crystal\n#C15+#C20\nCloak of the Reanimated Jester (2/5)\n#C15=#C20\n<img src=rdb://274687>\n#L "Cloak of the Reanimated Jester (3/5)" "274720"\n\nAwakened Soul Crystal\n#C15+#C20\nCloak of the Reanimated Jester (3/5)\n#C15=#C20\n<img src=rdb://274687>\n#L "Cloak of the Reanimated Jester (4/5)" "274721"\n\nAwakened Soul Crystal\n#C15+#C20\nCloak of the Reanimated Jester (4/5)\n#C15=#C20\n<img src=rdb://274687>\n#L "Cloak of the Reanimated Jester (5/5)" "274722"');
INSERT INTO recipes (id, name, author, recipe) VALUES (604, 'De-Hacked Frederickson Micro-kinetic Sleeves', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 Hack the sleeves with a hacker tool in order to make the clan\nversion of the omni sleeves. Neutrals can not wear them.\nNo trade skills required and the QL of the hacker tool does not matter.\n#C12 *** #C20\n\n#L "Frederickson Micro-kinetic Sleeves" "152710"\n<img src=rdb://81990>\n\n#L "Advanced Hacker Tool" "87814"\n<img src=rdb://99282>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nFrederickson Micro-kinetic Sleeves\n#C15+#C20\nAdvanced Hacker Tool\n#C15=#C20\n<img src=rdb://81990>\n#L "De-Hacked Frederickson Micro-kinetic Sleeves" "286303"\n#C16Skills: No skills required.#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (605, 'Hacked Light Omni-Tek Artillery Tank Armor', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 Since the standard issue can only be worn by Omni-Tek\npersonnel, several groups started to develop ways of enabling it to be worn\nby any faction.\nSoon, not one, but two methods were developed which removed the OT\nrestriction. Unfortunately both processes double the run speed handicap.\n#C12 *** #C20\n\n#L "Light Omni-Tek Artillery Tank Armor" "204987"\n<img src=rdb://41170>\n\n#L "Advanced Hacker Tool" "87814"\n<img src=rdb://99282>\n\n#L "Field Quantum Physics All-Purpose Tool" "164558"\n<img src=rdb://20410>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nLight Omni-Tek Artillery Tank Armor\n#C15+#C20\nAdvanced Hacker Tool\n#C15=#C20\n<img src=rdb://22393>\n#L "Hacked Light Omni-Tek Artillery Tank Armor" "204990"\n#C16Skills: Hacker tool needs to be at least 40% the ql of Tank Armor.\nYou''ll need 5x Breaking and Entering skill to hack it.#C20\n\nLight Omni-Tek Artillery Tank Armor\n#C15+#C20\nField Quantum Physics All-Purpose Tool\n#C15=#C20\n<img src=rdb://22393>\n#L "Hacked Light Omni-Tek Artillery Tank Armor" "204990"\nFQP tool needs to be at least 40% the ql of Tank Armor. You''ll need 5x Field\nQuantum Physics skill to hack it.');
INSERT INTO recipes (id, name, author, recipe) VALUES (606, 'Molybdenum Plate Armor Upgrades', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C16 Upgrade your Molybdenum Plate Armor (from Biodomes) with Black Molybdenum-Matrix of Xan and White Molybdenum-Matrix of Xan from Inside the\nMachine.#C20\n\n#L "Jathos'' Molybdenum Plate Vest" "252001"\n<img src=rdb://252433>\n\n#L "Kegern''s Molybdenum Plate Helmet" "252017"\n<img src=rdb://252429>\n\n#L "Jathos'' Molybdenum Plate Boots" "252002"\n<img src=rdb://252431>\n\n#L "Kegern''s Molybdenum Plate Gloves" "252018"\n<img src=rdb://252440>\n\n#L "Black Molybdenum-Matrix of Xan" "272458"\n<img src=rdb://272534>\n\n#L "White Molybdenum-Matrix of Xan" "272459"\n<img src=rdb://272535>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nJathos'' Molybdenum Plate Vest\n#C15+#C20\nBlack Molybdenum-Matrix of Xan\n#C15=#C20\n<img src=rdb://272440>\n#L "Enhanced Jathos'' Molybdenum Plate Vest" "272444"\n#C16Skills: | ? |#C20\n\nKegern''s Molybdenum Plate Helmet\n#C15+#C20\nBlack Molybdenum-Matrix of Xan\n#C15=#C20\n<img src=rdb://272431>\n#L "Enhanced Kegern''s Molybdenum Plate Helmet" "272454"\n#C16Skills: | ? |#C20\n#C16Note: Black Molybdenum-Matrix of Xan is used to upgrade both types of armor, Kegern''s as well as Jathos, but only vest and helmet pieces. And if you have already upgraded helmet with Action Probability Estimator from Apf, you won''t be able to do this combine.#C20\n\n#C16------------------------------#C20\n\nJathos'' Molybdenum Plate Boots\n#C15+#C20\nWhite Molybdenum-Matrix of Xan\n#C15=#C20\n<img src=rdb://272442>\n#L "Enhanced Jathos'' Molybdenum Plate Boots" "272445"\n#C16Skills: | ? |#C20\n\nKegern''s Molybdenum Plate Gloves\n#C15+#C20\nWhite Molybdenum-Matrix of Xan\n#C15=#C20\n<img src=rdb://272433>\n#L "Enhanced Kegern''s Molybdenum Plate Gloves" "272453"\n#C16Skills: | ? |#C20\n#C16Note: White Molybdenum-Matrix of Xan is used to upgrade both types of armor, Kegern''s as well as Jathos, but only boots, gloves, sleeves and pant pieces.#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (607, 'Photon Embedded Cloak', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 Your shining light in the darkness!\n#C12 *** #C20\n\n#L "Light Bar" "252157"\n<img src=rdb://37996>\n#C15or#C20\n#L "A Never-ending Being of Light" "262604"\n<img src=rdb://262646>\n\n#L "White Trenchcoat" "81969"\n<img src=rdb://82001>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nLight Bar\n#C15+#C20\nWhite Trenchcoat\n#C15=#C20\n<img src=rdb://281785>\n#L "Photon Embedded Cloak" "281777"\n#C16Skills: | None | #C20\n#C15or#C20\nA Never-ending Being of Light\n#C15+#C20\nWhite Trenchcoat\n#C15=#C20\n<img src=rdb://281785>\n#L "Photon Embedded Cloak" "281777"\n#C16Skills: | None | #C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (608, 'Cranium Nano Cloak', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 The Red Cranium Nano Cloak is a Clan & Meta-Physicist only\nitem but you can remove some of these requirements. Doing so however will\nalso remove or lower some of the modifiers: Nano Init. and MaxNanoEnergy.\nThe quality of the Field Quantum Physics All-Purpose Tool and Hacker Tool\nmust be at least 40% of QL of the cloak.\n#C12 *** #C20\n\n#L "Red Cranium Nano Cloak" "204996"\n<img src=rdb://85941>\n\n#L "Field Quantum Physics All-Purpose Tool" "164558"\n<img src=rdb://20410>\n\n#L "Advanced Hacker Tool" "87814"\n<img src=rdb://99282>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nRed Cranium Nano Cloak\n#C15+#C20\nField Quantum Physics All-Purpose Tool\n#C15=#C20\n<img src=rdb://85942>\n#L "Grey Cranium Nano Cloak" "204998"\n#C16Skills: |FQP tool needs to be at least 40% the ql of Tank Armor. You''ll\nneed 5x Field Quantum Physics skill to hack it.|#C20\n\n\nGrey Cranium Nano Cloak\n#C15+#C20\nAdvanced Hacker Tool\n#C15=#C20\n<img src=rdb://85945>\n#L "Grey Nano Cloak" "205000"\n#C16Skills: |Hacker Tool needs to be at least 40% of the ql of the Cloak.\nRequires 6X of cloaks QL of Breaking & Entering and the QL of the.|#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (609, 'Enhanced Pads of Dedication', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 Omni-Tek employees and Neutrals will be happy to know that their Pads of Dedication which can be obtained by doing the Shoulderpads Quest can be upgraded even further. What''s even better is that the upgrade doesn''t require any skills to do.\n\nAll you will need besides your Pads is an Omnifier. You can find it in missions, it''s a common from mobs and you can also find it in chests.\n#C12 *** #C20\n\n#L "Omnifier" "208312"\n<img src=rdb://156479>\n\n#L "Pads of Dedication" "157998"\n<img src=rdb://31537>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nOmnifier\n#C15+#C20\nPads of Dedication\n#C15=#C20\n<img src=rdb://31537>\n#L "Enhanced Pads of Dedication" "209237"\n#C16Skills: | None | #C20\n\nComparing stats between the original and upgraded versions, most of them have been enhanced by 50%. Oh and the Pads turn into a nice shiny black color which perfectly matches the popular Omni-Tek Armed Forces armor omnis wear.');
INSERT INTO recipes (id, name, author, recipe) VALUES (610, 'Alappaa shoulderpads', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 You''ll get parts for upgrade in Alappaa and in Dustbrigade Outpost. There are 3 different types you can upgrade them into. But first you need to unfreeze them.\n#C12 *** #C20\n\n#L "Frozen Shoulderpads" "269896"\n<img src=rdb://269968>\n\n#L "Permafrost Melting Tool" "269805"\n<img src=rdb://269844>\n\n#L "Permafrost Armor Component" "269937"\n<img src=rdb://269936>\n\n#L "Combat Program" "269946"\n<img src=rdb://269949>\n#C15or#C20\n#L "Nano Technology Program" "269945"\n<img src=rdb://269950>\n#C15or#C20\n#L "Melee Program" "269947"\n<img src=rdb://269948>\n\n#C15-And the upgrades-#C20\n\n#L "Master Combat Program" "269960"\n<img src=rdb://269949>\n#C15or#C20\n#L "Master Nano Technology Program" "269959"\n<img src=rdb://269950>\n#C15or#C20\n#L "Master Melee Program" "269961"\n<img src=rdb://269948>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n#C16 Unfreezing the shoulderpads #C20\n\nFrozen Shoulderpads\n#C15+#C20\nPermafrost Melting Tool\n#C15=#C20\n<img src=rdb://269968>\n#L "Shoulderpads of the Icy Wastes" "269897"\n#C16Skills: | None | #C20\n\n#C16 Superior Icy Shoulderpads of Explosive Power #C20\n\nCombat Program\n#C15+#C20\nPermafrost Armor Component\n#C15=#C20\n<img src=rdb://269936>\n#L "Combat Attuned Permafrost Armor Component" "269938"\n#C16Skills: | 1500 ME | #C20\n\nShoulderpads of the Icy Wastes\n#C15+#C20\nCombat Attuned Permafrost Armor Component\n#C15=#C20\n<img src=rdb://269963>\n#L "Icy Shoulderpads of Explosive Power" "269898"\n#C16Skills: | none | #C20\n\nIcy Shoulderpads of Explosive Power\n#C15+#C20\nMaster Combat Program\n#C15=#C20\n<img src=rdb://269963>\n#L "Superior Icy Shoulderpads of Explosive Power" "269899"\n#C16Skills: | none | #C20\n\n#C16 Superior Icy Shoulderpads of the Powerful Mind #C20\n\nNano Technology Program\n#C15+#C20\nPermafrost Armor Component\n#C15=#C20\n<img src=rdb://269936>\n#L "Nano Attuned Permafrost Armor Component" "269939"\n#C16Skills: | 1500 ME | #C20\n\nShoulderpads of the Icy Wastes\n#C15+#C20\nNano Attuned Permafrost Armor Component\n#C15=#C20\n<img src=rdb://269962>\n#L "Icy Shoulderpads of Explosive Power" "269898"\n#C16Skills: | none | #C20\n\nIcy Shoulderpads of the Powerful Mind\n#C15+#C20\nMaster Nano Technology Program\n#C15=#C20\n<img src=rdb://269962>\n#L "Superior Icy Shoulderpads of the Powerful Mind" "269900"\n#C16Skills: | none | #C20\n\n#C16 Superior Icy Shoulderpads of Brawn #C20\n\nMelee Program\n#C15+#C20\nPermafrost Armor Component\n#C15=#C20\n<img src=rdb://269936>\n#L "Melee Attuned Permafrost Armor Component" "269940"\n#C16Skills: | 1500 ME | #C20\n\nShoulderpads of the Icy Wastes\n#C15+#C20\nMelee Attuned Permafrost Armor Component\n#C15=#C20\n<img src=rdb://269964>\n#L "Icy Shoulderpads of Brawn" "269902"\n#C16Skills: | none | #C20\n\nIcy Shoulderpads of Brawn\n#C15+#C20\nMaster Melee Program\n#C15=#C20\n<img src=rdb://269964>\n#L "Superior Icy Shoulderpads of Brawn" "269903"\n#C16Skills: | none | #C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (611, 'De''Valos Protection Ring', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 Drops from the Bosses in Sector 35.#C12 *** #C20\n\n#L "De''Valos Radiation Protection Ring" "257117"\n<img src=rdb://281266>\n\n#L "De''Valos Lava Protection Ring" "257116"\n<img src=rdb://281266>\n\n#L "Hold Hell at Bay" "231234"\n<img src=rdb://234505>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n#C16 This tradeskill process does not require any skill whatsoever, just combine rings. #C20\n\nDe''Valos Radiation Protection Ring\n#C15+#C20\nDe''Valos Lava Protection Ring\n#C15=#C20\n<img src=rdb://281266>\n#L "De''Valos Radiation and Lava Protection Ring" "281264"\n#C16Skills: | None | #C20\n\nDe''Valos Radiation and Lava Protection Ring\n#C15+#C20\nHold Hell at Bay\n#C15=#C20\n<img src=rdb://281266>\n#L "De''Valos Protection Ring" "281267"\n#C16Skills: | None | #C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (612, 'Nano Breed Service Suit Armor', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 Before you go on, this armor is one of rarest found on Rubi-Ka. So far only handfull of pieces were found, even if it made ingame years ago (only confirmed drop is reported from lvl 59 Nano Breed mob inside mission). And since it''s limited buffs, mostly collectors would be interested in it. #C12 *** #C20\n\n#L "Nano Breed Survival Suit Body Armor" "206609"\n<img src=rdb://13252>\n\n#L "Nano Breed Survival Suit Boots" "206610"\n<img src=rdb://155112>\n\n#L "Nano Breed Survival Suit Pants" "206611"\n<img src=rdb://13302>\n\n#L "Nano Breed Survival Suit Sleeves" "206608"\n<img src=rdb://22949>\n\n#L "Inactive OT Metamorphing Liquid Nanobots" "164952"\n<img src=rdb://156496>\n\n#L "Nano Programming Interface" "161699"\n<img src=rdb://99279>\n\n#L "Advanced Hacker Tool" "87814"\n<img src=rdb://99282>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n#C16 The Nano Programming Interface can be bought from tools terminals. Inactive OT Metamorphing Liquid Nanobots are in armor and clothing components terminals. Hacker Tool drop from missions and can also be purchased at regular shops. \n\nFirst, you need to prepare OT Metamorphing Liquid Nanobots. If you want fully augmented armor, you''ll need 10 pieces of them for each armor piece you''re making. Nano Programming Interface doesn''t get used up in the process.#C20\n\nInactive OT Metamorphing Liquid Nanobots\n#C15+#C20\nNano Programming Interface\n#C15=#C20\n<img src=rdb://156492>\n#L "Activated OT Metamorphing Liquid Nanobots" "164954"\n#C16Skills: | x2.5 CL x4 NP based on QL of Nanobots | #C20\n#C16Notes: Inactive OT Nanobots must be within 90% of armor. #C20\n\n#C16 Once this is sorted, you''re ready to start on armor itself.#C20\n\nNano Breed Survival Suit Body Armor\n#C15+#C20\nAdvanced Hacker Tool\n#C15=#C20\n<img src=rdb://13252>\n#L "Hacked Nano Breed Survival Suit Body Armor" "206614"\n#C16Skills: | 48 BE | #C20\n\nHacked Nano Breed Survival Suit Body Armor\n#C15+#C20\nActivated OT Metamorphing Liquid Nanobots\n#C15=#C20\n<img src=rdb://13252>\n#L "Nano Breed Service Suit Body Armor - Once Augmented" "206645"\n#C16Skills: | 60 NP | #C20\n\n#C16 You can repeat this process 9 more times each time after the 2nd upgrade the NP increases by 60 points to a final skill of 540 NP on final upgrade.#C20\n\nNano Breed Service Suit Body Armor - Nine Times Augmented\n#C15+#C20\nActivated OT Metamorphing Liquid Nanobots\n#C15=#C20\n<img src=rdb://13252>\n#L "Nano Breed Service Suit Body Armor - Maximum Augmented" "206636"\n#C16Skills: | 540 NP | #C20\n\n#C16 If you manage to collect all 5 pieces of armor (4 different pieces and extra sleeve, since there aren''t that many one-armed characters around) and upgrade them to maximum, you''ll get bonus of:\n\nMaxNanoEnergy: 1100\nNanoC.Init: 250\n\nWith that, only practical use for it, that I can think of, is maybe some NT or Doctor twink, but they would still be penalized for lack of AC or Nano Skill mods on this armor.#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (613, 'Sabotaging Surgeons Cloak', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 In order to sabotage anything, you need to get your hands on it first. In case of Omni-Med Field Surgeons Cloak, it drops off Dyna and mission bosses. It drops in various ql, highest found so far being ql 200. Whole sabotaging business is actually really easy, all you need is Advanced Hacker Tool. #C12 *** #C20\n\n#L "Omni-Med Field Surgeons Cloak" "201235"\n<img src=rdb://120623>\n\n#L "Advanced Hacker Tool" "87814"\n<img src=rdb://99282>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nOmni-Med Field Surgeons Cloak\n#C15+#C20\nAdvanced Hacker Tool\n#C15=#C20\n<img src=rdb://120623>\n#L "Sabotaged Omni-Med Field Surgeons Cloak" "201238"\n#C16Skills: | 4.75 BE 3.25x CL based on QL | #C20\n\n#C16 If person doing tradeskills have high enough skills, end product can be higher ql as 200. Hacking the cloak also results in loosing Omni tag and BiologicalMetamorphosis buff. #C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (614, 'Sureshot Glasses', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C12 *** #C16 Sureshot Glasses drop from Alien generals. But in case you''re tired of farming them, there''s a way to build them. #C12 *** #C20\n\n#L "Wire Drawing Machine" "150917"\n<img src=rdb://149952>\n\n#L "Personal Furnace" "150930"\n<img src=rdb://290713>\n\n#L "Small Platinum Ingot" "151363"\n<img src=rdb://289751>\n\n#L "Crystalline Disk" "253139"\n<img src=rdb://100319>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n#C16 Now it''s time to put everything together: #C20\n\nSmall Platinum Ingot\n#C15+#C20\nPersonal Furnace\n#C15=#C20\n<img src=rdb://290708>\n#L "Liquid Platinum" "151361"\n#C16Skills: | 3.8x ME based on QL | #C20\n\nLiquid Platinum\n#C15+#C20\nWire Drawing Machine\n#C15=#C20\n<img src=rdb://151017>\n#L "Platinum Filigree Wire" "151360"\n#C16Skills: | 5.5x ME, 1.8x Agility | #C20\n\nCrystalline Disk\n#C15+#C20\nPlatinum Filigree Wire\n#C15=#C20\n<img src=rdb://86483>\n#L "Sureshot Glasses" "253183"\n#C16Skills: | 5x EE,ME | #C20\n\n\n#C16 At start of guide we said you''ll need at least ql 100 Crystalline Disk. Why? Simply because Sureshot Glasses come in quality range of 100-300. Another thing to watch while making them is title requirement to wear (ql 100-119 for tlt1, 120-159 for tlt2, 160-199 for tlt3, 200-239 for tlt4, 240-279 for tlt5, 280+ for tlt6). #C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (615, 'Aggression Trimmer', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C16 Every Engineer wants to make his/her bot as powerful as possible, the reason why trimming bots is one of the most common tactics to enhance bots abilities. Trimming however does come at a price, by boosting one part of the bot''s functions another is lowered. For example, if you use a Trimmer to raise the defense of your bot then the aggressiveness of it will be lowered. Trimmers can be purchased from general shops, but this specific Trimmer can only be built, and it has one advantage...it will increase the chances of your bot keeping aggro on whatever it''s fighting, at no cost of other skills. This means an Engineer will be less likely of getting attacked leaving his/her bot to do all the fighting. The lowest version Aggression Trimmer you''ll be able to assemble is QL 30, keep that in mind when buying parts. #C20\n\n#L "Chemical Impact Injector" "142911"\n<img src=rdb://130815>\n\n#L "XU-11 Serum" "154909"\n<img src=rdb://100338>\n\n#L "Trimmer Casing" "154938"\n<img src=rdb://154935>\n\n#C16 All of them can be found at general shops. It could cost you a few hundred thousand credits, so be sure to have funds to make the purchases. #C20\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n#C16 Now it''s time to put everything together: #C20\n\nXU-11 Serum\n#C15+#C20\nChemical Impact Injector\n#C15=#C20\n<img src=rdb://130814>\n#L "Modified Chemical Impact Injector" "154911"\n#C16Skills: | 1.8x CHEM based on QL | #C20\n#C16Note: The XU-11 Serum needs to be within 60% of the QL of the Chemical Impact Injector for this combine to work. Note that the Modified Chemical Impact Injector will raise a few QLs during this process (use the Tradeskill Kit to check). #C20\n\nModified Chemical Impact Injector\n#C15+#C20\nTrimmer Casing\n#C15=#C20\n<img src=rdb://292747>\n#L "Trimmer - Increase Aggressiveness" "154940"\n#C16Skills: | 3.7x ME* | #C20\n\n#C16 The Modified Chemical Impact Injector needs to be within 90% of the QL of the Trimmer Casing for this combine to work. Use the Tradeskill Kit to adjust the final QL of the trimmer.\n\nThe Trimmer - Increase Aggressiveness requires Mechanical Engineering and Computer Literacy to use. Also the Trimmer only works on robotic pets... not on Metaphysicist pets.\nA quality level 200 Trimmer requires 751 Mechanical Engineering and 551 Computer Literacy\n\nNote: Remember that as you upgrade your bot to a strong version, you should do the same with the Trimmer.\n\n* May also require a small amount of Computer Literacy (less than x1.6).  #C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (616, 'AI Yalms', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C16 Looking for a snazzy new yalm? Our friend Uncle Bazzit is selling a few new yalm upgrades, for a price of course.\nHead to Meetmedere, to Uncle Bazzit''s shop. The upgrades are found in the shop terminal, in the back left of the room.#C20\n\n#L "Yalmaha 29600 - Obsidian Upgrade Kit" "254817"\n<img src=rdb://20406>\n\n#L "Yalmaha 29600 - Pearl Upgrade Kit" "254818"\n<img src=rdb://20406>\n\n#L "Yalmaha 29610 - Sunrise Upgrade Kit" "254819"\n<img src=rdb://81786>\n\n#L "Yalmaha 29610 - Daylight Upgrade Kit" "254820"\n<img src=rdb://81786>\n\n#L "Yalmaha 29610 - Sunset Upgrade Kit" "254821"\n<img src=rdb://81786>\n\n#L "Yalmaha 29610 - Dusk Upgrade Kit" "254823"\n<img src=rdb://81786>\n\n#L "Yalmaha XL - 29478" "96088"\n<img src=rdb://38023>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n#C16Note:End QL of the yalm is determined by Comp Lit. To make a ql300 (results in lowest vehicle air), you will need 1500 CL.\nYou will need at least 750 Computer Literature to make lowest ql available. Ql of Yalmaha XL - 29478 you use, doesn not matter#C20\n\nYalmaha 29600 - Obsidian Upgrade Kit\n#C15+#C20\nYalmaha XL - 29478\n#C15=#C20\n<img src=rdb://255895>\n#L "Yalmaha - 29600 - Obsidian" "253582"\n#C16Skills: | CL |#C20\n\nYalmaha 29600 - Pearl Upgrade Kit\n#C15+#C20\nYalmaha XL - 29478\n#C15=#C20\n<img src=rdb://255900>\n#L "Yalmaha - 29600 - Pearl" "253584"\n#C16Skills: | CL |#C20\n\nYalmaha 29610 - Sunrise Upgrade Kit\n#C15+#C20\nYalmaha XL - 29478\n#C15=#C20\n<img src=rdb://255896>\n#L "Yalmaha - 29610 - Sunrise" "253574"\n#C16Skills: | CL |#C20\n\nYalmaha 29610 - Daylight Upgrade Kit\n#C15+#C20\nYalmaha XL - 29478\n#C15=#C20\n<img src=rdb://255898>\n#L "Yalmaha - 29610 - Daylight" "253576"\n#C16Skills: | CL |#C20\n\nYalmaha 29610 - Sunset Upgrade Kit\n#C15+#C20\nYalmaha XL - 29478\n#C15=#C20\n<img src=rdb://255897>\n#L "Yalmaha - 29610 - Sunset" "253578"\n#C16Skills: | CL |#C20\n\nYalmaha 29610 - Dusk Upgrade Kit\n#C15+#C20\nYalmaha XL - 29478\n#C15=#C20\n<img src=rdb://255899>\n#L "Yalmaha - 29600 - Dusk" "253580"\n#C16Skills: | CL |#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (620, 'Mass Relocating Robot', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C16 There are 5 different Mass Relocating Robots. Each one requires a different Instruction Disc or Programmed Photon Particle Emitter.\nThe weapon PPPE''s drop from level 10-30 mobs, the Shape Hard/Soft Armor PPPE''s are harder to find as they drop from QL 60 dyna and mission bosses.#C20\n\n#L "Robot Instruction Disc (Improve Crushing Weapons)" "155906"\n<img src=rdb://83347>\n\n#L "Robot Instruction Disc (Improve Thrusting Weapons)" "155907"\n<img src=rdb://83443>\n\n#L "Robot Instruction Disc (Improve Slashing Weapons)" "155533"\n<img src=rdb://83446>\n\n#L "Programmed Photon Particle Emitter (Shape Hard Armor)" "162221"\n<img src=rdb://144466>\n\n#L "Programmed Photon Particle Emitter (Shape Soft Armor)" "162222"\n<img src=rdb://144476>\n\n#L "Symbol Library - Space" "144790"\n<img src=rdb://72782>\n\n#L "Photon Particle Emitter" "144812"\n<img src=rdb://83869>\n\n#L "Ultimate Mass Relocating Robot" "155592"\n<img src=rdb://155928>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\n#C16Note: When making a Weapon MRR, you will need to upgrade the Robot Instruction Disks to Programmed Photon Particle Emitters like this:#C20\n\nRobot Instruction Disc (Improve Crushing Weapons)\n#C15+#C20\nSymbol Library - Space\n#C15=#C20\n<img src=rdb://83052>\n#L "Robot Compiled Algorithm (Improve Crushing Weapons)" "155903"\n#C16Skills: | 4 x QL of the Instruction Disk in NP and CL |#C20\n#C16Note: QL of the Symbol Library must be at least 25% of the Robot Instruction Disk#C20\n\nRobot Compiled Algorithm (Improve Crushing Weapons)\n#C15+#C20\nPhoton Particle Emitter\n#C15=#C20\n<img src=rdb://83640>\n#L "Programmed Photon Particle Emitter (Improve Crushing Weapons)" "155905"\n#C16Skills: | 4.25 x QL of the Instruction Disk in NP |#C20\n#C16Note: QL of the Photon Particle Emitter must be at least 50% of the Robot Instruction Disk#C20\n\n#C15------------------------------#C20\n\n#C16Now combine your PPPE (XXX) with an Ultimate Mass Relocating Robot:#C20\n\nProgrammed Photon Particle Emitter (Improve Crushing Weapons)\n#C15+#C20\nUltimate Mass Relocating Robot\n#C15=#C20\n<img src=rdb://155927>\n#L "Mass Relocating Robot (Improve Crushing Weapons)" "155924"\n');
INSERT INTO recipes (id, name, author, recipe) VALUES (621, 'Ancient Defender', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C16 Both parts required for this process can be found in Albtraum#C20\n\n#L "Empty Ancient Device" "267709"\n<img src=rdb://218753>\n\n#L "Crystalised Memories of a Defender" "267711"\n<img src=rdb://72771>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nEmpty Ancient Device\n#C15+#C20\nCrystalised Memories of a Defender\n#C15=#C20\n<img src=rdb://218752>\n#L "Ancient Defender" "267627"\n#C16Skills: | 1250 CL |#C20\n\n#C16Note: You can upgrade this item with a #L "Dust Brigade Notum Infuser" "274552"\nThe recipe can be found here: #L "Infused Ancient Defender" "/tell <myname> rshow 628"#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (622, 'Ancient Medical Bracer', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C16 All parts required for this process can be found in Albtraum. \nYou will need to build an Ancient Engineering Device as well if you do not have one already. \nA link to this tradeskill process is included#C20\n\n#L "Crystalised Memories of a Doctor" "267726"\n<img src=rdb://72771>\n\n#L "Crystalised Memories of a Surgeon" "267728"\n<img src=rdb://72771>\n\n#L "Ancient Scrap of Spirit Knowledge" "267677"\n<img src=rdb://163575>\n\n#L "Ancient Scrap of Saturated Spirit Knowledge" "267679"\n<img src=rdb://163575>\n\n#L "Inactive Ancient Bracer" "267746"\n<img src=rdb://290853>\n\n#L "Inert Knowledge Crystal" "267737"\n<img src=rdb://151030>\n\n#L "Ancient Engineering Device" "267751"\n<img src=rdb://156095>\n#C16Note:To build your Ancient Engineering Device, go here:\n#L "Ancient Engineering Device" "/tell <myname> rshow 594"#C20\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nCrystalised Memories of a Doctor\n#C15+#C20\nCrystalised Memories of a Surgeon\n#C15=#C20\n<img src=rdb://72769>\n#L "Combined Crystalised Medical Memories" "267729"\n#C16Skills: | 1750 CH |#C20\n\nAncient Scrap of Spirit Knowledge\n#C15+#C20\nAncient Scrap of Saturated Spirit Knowledge\n#C15=#C20\n<img src=rdb://163575>\n#L "Ancient Scrap of Condensed Spirit Knowledge" "267680"\n#C16Skills: | 1750 PT |#C20\n\nAncient Scrap of Condensed Spirit Knowledge\n#C15+#C20\nCombined Crystalised Medical Memories\n#C15=#C20\n<img src=rdb://163575>\n#L "Ancient Scrap of Spirit Medical Knowledge" "267727"\n#C16Skills: | 1750 CH |#C20\n\nAncient Scrap of Spirit Medical Knowledge\n#C15+#C20\nInert Knowledge Crystal\n#C15=#C20\n<img src=rdb://151030>\n#L "Medical Knowledge Crystal" "267738"\n#C16Skills: | 1750 CH |#C20\n\nAncient Engineering Device\n#C15+#C20\nInactive Ancient Bracer\n#C15=#C20\n<img src=rdb://290847>\n#L "Active Ancient Bracer" "267747"\n#C16Skills: | 1500 ME |#C20\n\nActive Ancient Bracer\n#C15+#C20\nMedical Knowledge Crystal\n#C15=#C20\n<img src=rdb://290851>\n#L "Ancient Medical Bracer" "267752"\n#C16Skills: | 1250 ME |#C20');
INSERT INTO recipes (id, name, author, recipe) VALUES (623, 'Ancient Nano Enhancer', '', '#C16------------------------------\n#C12Ingredients #C20\n#C16------------------------------#C20\n\n#C16 Both parts required for this process can be found in Albtraum#C20\n\n#L "Empty Ancient Device" "267709"\n<img src=rdb://218753>\n\n#L "Crystalised Memories of a Scientist" "267713"\n<img src=rdb://72771>\n\n#C16------------------------------\n#C12Recipe #C16\n#C16------------------------------#C20\n\nEmpty Ancient Device\n#C15+#C20\nCrystalised Memories of a Scientist\n#C15=#C20\n<img src=rdb://218768>\n#L "Ancient Nano Enhancer" "267712"\n#C16Skills: | 1250 CL |#C20\n\n#C16Note: You can upgrade this item with a #L "Dust Brigade Notum Infuser" "274552"\nThe recipe for this can be found here:#L "Infused Ancient Nano Enhancer" "/tell <myname> rshow 630"#C2');
