INSERT INTO recipes2 (id, aou_id, name, recipe) VALUES (88,0,'Prowler Armor','<font color=#FF0000> Ingredients </font><font color=#009B00>\n    - ( ie: <img src=''rdb://13259''> <a href=''chatcmd:///tell <myname> show me item 245123''>Crawler Body Armor</a> ) a piece of Crawler Armor ( <a href=''chatcmd:///tell <myname> show me 37''>see here</a> )\n    - <img src=''rdb://205534''> <a href=''chatcmd:///tell <myname> show me item 245215''>Damaged Proliferation Unit</a>\n    - <img src=''rdb://151011''> <a href=''chatcmd:///tell <myname> show me item 150922''>Screwdriver</a>\n     \n</font><font color=#FF0000> Recipe </font><font color=#FFFF00>\n    - |  | <img src=''rdb://151011''></font><font color=#FF0000> * </font><font color=#FFFF00> + <img src=''rdb://205534''> = <img src=''rdb://205533''> | <a href=''chatcmd:///tell <myname> show me item 150922''>Screwdriver</a></font><font color=#FF0000> * </font><font color=#FFFF00> + <a href=''chatcmd:///tell <myname> show me item 245215''>Damaged Proliferation Unit</a> = <a href=''chatcmd:///tell <myname> show me item 245216''>Proliferation Unit</a>\n    - |  | <img src=''rdb://205533''> + <img src=''rdb://13259''> = <img src=''rdb://13259''> | <a href=''chatcmd:///tell <myname> show me item 245216''>Proliferation Unit</a> + <a href=''chatcmd:///tell <myname> show me item 245123''>Crawler Body Armor</a> = <a href=''chatcmd:///tell <myname> show me item 245178''>Prowler Body Armor</a>\n    \n</font><font color=#FF0000> Details</font><font color=#FFFF00>\n	- <img src=''rdb://37423''> <a href=''chatcmd:///tell <myname> show me item 245176''>Prowler Armor Sleeves</a>\n	- <img src=''rdb://13259''> <a href=''chatcmd:///tell <myname> show me item 245178''>Prowler Body Armor</a>\n	- <img src=''rdb://37630''> <a href=''chatcmd:///tell <myname> show me item 245180''>Prowler Armor Gloves</a>\n	- <img src=''rdb://13288''> <a href=''chatcmd:///tell <myname> show me item 245182''>Prowler Armor Pants</a>\n	- <img src=''rdb://37596''> <a hr